definition.script:
pipeline {
    agent any

    stages {
        stage('Preparar entorno') {
            steps {
                echo 'Preparando entorno...'
                script {
                    // Instalar dependencias del proyecto
                    sh 'docker exec libreria-app-php-1 composer install --no-interaction --prefer-dist --optimize-autoloader'
                    // Generar clave de la aplicación si no existe
                    sh 'docker exec libreria-app-php-1 php artisan key:generate'
                    // Migrar la base de datos
                    sh 'docker exec libreria-app-php-1 php artisan migrate:fresh --seed'
                }
            }
        }

        stage('Correr pruebas unitarias') {
            steps {
                echo 'Corriendo pruebas unitarias...'
                script {
                    sh 'docker exec libreria-app-php-1 php artisan test'
                }
            }
        }

        stage('Correr pruebas Dusk') {
            steps {
                echo 'Corriendo pruebas Dusk...'
                script {
                    sh 'docker exec libreria-app-php-1 php artisan dusk'
                }
            }
        }
    }

    post {
        always {
            echo 'Limpiando entorno...'
            script {
                sh 'docker exec libreria-app-php-1 php artisan config:clear'
                sh 'docker exec libreria-app-php-1 php artisan cache:clear'
            }
        }
        success {
            echo 'Pipeline ejecutado con éxito.'
        }
        failure {
            echo 'El pipeline falló.'
        }
    }
}
